import React from 'react';
import PropTypes, { any } from 'prop-types';

import Task from '../Task';

import './task-list.css';

function TodoList({ todos, onDeleted, onToggleDone, editTask
 // , timerOnGo 



}) {

  // eslint-disable-next-line class-methods-use-this
  const timerOnGo = (id, secsLeft) => {
    console.log(`id: ${id}, secs left: ${secsLeft}`)
  }
  const todoList = todos.map((todo) => {
    const { id } = todo;
    return (
      <Task
        key={id}
        todo={todo}
        onDeleted={() => onDeleted(id)}
        onToggleDone={() => onToggleDone(id)}
        editTask={(idE, newDescription) => editTask(idE, newDescription)}
        timerOnGo = {(idT, timeLeft) => timerOnGo(idT, timeLeft)}
      />
    );
  });

  return <ul className="todo-list">{todoList}</ul>;
}

TodoList.defaultProps = {
  todos: [
    {
      id: '0',
      done: false,
      description: 'empty task',
      secondsToDo: 0,
      createdTime: new Date(),
    },
  ],
};

TodoList.propTypes = {
  todos: PropTypes.arrayOf(PropTypes.objectOf(any)),
  onDeleted: PropTypes.func.isRequired,
  onToggleDone: PropTypes.func.isRequired,
  editTask: PropTypes.func.isRequired,
  // timerOnGo: PropTypes.func.isRequired
};

export default TodoList;
