import React, { useState, createRef } from 'react';

import './new-task-form.css';

export default function NewTaskForm() {

  // inputValue: '',
  // valueMin: '0',
  // valueSec: '0',

  const [ input, setInput ] = useState('');
  const [ inputMin, setInputMin ] = useState('0');
  const [ inputSec, setInputSec ] = useState('0');

  const inputRef = createRef();
  const inputMinRef = createRef();
  const inputSecRef = createRef();

  // const handleSubmit = (event) => {
  //   event.preventDefault();
  //   const { inputValue, valueMin, valueSec } = this.state;
  //   const { addTask } = this.props;
  //   if (inputValue.trim() && Number(valueSec) < 60) {
  //     const secondsToDo = Number(valueMin) * 60 + Number(valueSec);
  //     addTask(inputValue, secondsToDo); // valueMin, valueSec
  //     this.setState(() => ({
  //       inputValue: '',
  //       valueMin: '',
  //       valueSec: '',
  //     }));
  //   } else {
  //     this.setState(() => ({
  //       inputValue: 'Your task ...',
  //     }));
  //   }
  // }

  const handleChange = () => {
    console.log()
    setInput(inputRef.current.value);
    setInputMin(inputMinRef.current.value);
    setInputSec(inputSecRef.current.value);
  }


  // const { inputValue, valueMin, valueSec } = this.state;
  return (
    <header>
      <h1>todos</h1>
      <form id="add_task_form" className="new-todo-form" > 
        <input
          className="new-todo"
          ref = {inputRef}
          id="inputValue"
          type="text"
          placeholder="What needs to be done...?"
          value={input}
          onChange={(e) => handleChange(e)}
        />
        <input
          className="new-todo-form__timer"
          ref = {inputMinRef}
          id="valueMin"
          type="number"
          placeholder="Min"
          value={inputMin}
          onChange={(e) => handleChange(e)}
        />
        <input
          className="new-todo-form__timer"
          ref = {inputMinRef}
          id="valueSec"
          type="number"
          placeholder="Sec"
          value={inputSec}
          onChange={(e) => handleChange(e)}
        />
        <button aria-label="submit-button" type="submit" />
      </form>
    </header>
  );
}
