import React from "react";

import PropTypes from 'prop-types';

// eslint-disable-next-line react/prefer-stateless-function
export default class Timer extends React.Component {
  constructor() {
    super();
    this.state = {
      secondsToDo: 0,
    };
  }

  componentDidMount() {
    const { secondsToDo } = this.props;
    if(secondsToDo > 0) {
      const countSeconds = Number(min) * 60 + Number(sec);
      this.setState({
        secondsToDo: countSeconds,
      })
    }
    setInterval(this.timerOnGo, 1000);
  }

  timerOnGo = () => {
    // eslint-disable-next-line react/destructuring-assignment
    const prevSec = this.state.secondsToDo;
    this.setState(() => ({
      secondsToDo: prevSec - 1,
    }))
  }

  getTimeStr = () => {
    const { secondsToDo } = this.state;
    const newMin = Math.floor(secondsToDo / 60);
    const newSec = secondsToDo - newMin * 60;
    return `${newMin}:${newSec}`;
  }

  render() {
    const { secondsToDo } = this.props;
    if (secondsToDo <= 0){
      return null;
    }

    const time = this.getTimeStr();

    return (
      <span className="description">
        <button aria-label="icon-play" type="button" className="icon icon-play" />
        <button aria-label="icon stop" type="button" className="icon icon-pause" />
        {time}
      </span>
    )
  }
};

Timer.defaultProps = {
  min: '0',
  sec: '0',
};
Timer.propTypes = {
  min: PropTypes.string,
  sec: PropTypes.string
}
