import {React, Component} from "react";
import NewTaskForm from "../NewTaskForm";
import AppMain from "../app-main";

import './app.css'

export default class App extends Component {
    maxId = 0;

    state = {
        todosData : [
            this.newTask('learn JS'),
            this.newTask('drink coffee')
        ]
    }

    newTask(task)  {
        return {
            id: this.maxId ++,
            done: false,
            status: 'active',
            description: task,
            createdTime: 'time ago'
        };
    };

    deleteTask = (id) => {
        this.setState(({ todosData, counter }) => {
            const idx = todosData.findIndex(el => el.id === id);
            const newData = [
                ...todosData.slice(0, idx),
                ...todosData.slice(idx + 1)
            ];
            return {
                todosData: newData
            }
        })
    }

    addTask = (task) => {
        this.setState(({ todosData }) => {
            const newTask = this.newTask(task)
            const newData = [...todosData, newTask];
            return {
                todosData: newData            
            }
        })
    }

    onToggleDone = (id) => {
        console.log(`toggleDone ${id}`);
        this.setState({  })
    };

    render () {
        return (
            <section className = 'todoapp'>
                <NewTaskForm addTask = {this.addTask}/>
                <AppMain todos = {this.state.todosData}
                        onDeleted={this.deleteTask}
                        onToggleDone = {this.onToggleDone}/>
            </section>
        );
    }
}
