import {React, Component} from "react";
import { formatDistanceToNow } from "date-fns";
import PropTypes from 'prop-types'
import './task.css'

class Task extends Component {
    constructor() {
      super();
      this.state = {
        editing: false,
        // eslint-disable-next-line react/no-unused-state
        value: '',
      };
    }

    async handleSubmit(event) {
      event.preventDefault();
      const { value } = this.state;
      const { editTask } = this.props;
      if (value.trim()){
          console.log(event.target.id, value);// func editTask(event.taget.id, value)
          editTask(event.target.id, value);
      }
      this.setState(() => ({
              value : ''
          })
      );
      this.setState(() => ({editing: false, value: ''}))
    }

    async onValueChange(event) {
      this.setState(() => ({
              value: event.target.value
          })
      );
  };

    render () {
        const { todo, onDeleted, onToggleDone} = this.props;
        const { id, done, description, createdTime } = todo;
        const { editing } = this.state;
        let status;

        if (done) {
            status = 'completed';
        } else {
            status = 'active';
        }

        if (editing === true) {
          status = 'editing';
        }

        return (
            <li className={status}>
                <div className="view">
                    <input className="toggle" 
                            id = {id}
                            type="checkbox"
                            checked={done} 
                            onChange={onToggleDone}/>
                    <label htmlFor={id}>
                        <span className="description">{ description }</span>
                        <span className="created">{ `created ${formatDistanceToNow(createdTime, {includeSeconds: true})} ago` }</span>
                    </label>
                    <button type="button"
                            aria-label="edit"
                            className="icon icon-edit"
                            // eslint-disable-next-line react/no-unused-state
                            onClick={() => this.setState(() => ({
                              editing: !editing,
                              // eslint-disable-next-line react/no-unused-state
                              value: description,
                            }))} />
                    <button type="button"
                            aria-label= "delete"
                            className="icon icon-destroy"
                            onClick={onDeleted} />
                </div>
                {status === 'editing' && (
                  <form id = {id} 
                        onSubmit={this.handleSubmit.bind(this)}>
                    <input id = {id} 
                            type="text" 
                            className="edit" 
                            defaultValue={description}
                            onChange={e => this.onValueChange(e)}/>
                  </form>
                )}
            </li>
        );
    }
}

Task.defaultProps = {
  todo : {
    id: 0,
    done: false,
    description: 'empty task',
    createdTime: new Date()
  }
}

Task.propTypes = {
  todo: PropTypes.shape({
    id: PropTypes.number,
    done: PropTypes.bool,
    description: PropTypes.string,
    createdTime: PropTypes.instanceOf(Date),
  }),
  onDeleted: PropTypes.func.isRequired,
  onToggleDone: PropTypes.func.isRequired,
  editTask: PropTypes.func.isRequired
}

export default Task;
