import React from "react";

import PropTypes from 'prop-types';

// eslint-disable-next-line react/prefer-stateless-function
export default class Timer extends React.Component {
  constructor() {
    super();
    this.state = {
      secondsToDo: 0,
    };
  }

  componentDidMount() {
    const { id, secondsToDo } = this.props;
    if (secondsToDo > 0) {
      const lastTime  = localStorage.getItem(id.toString());
      if (lastTime) {
        const last = new Date(lastTime)
        const now = new Date();
        const toGo = secondsToDo - Math.floor((now.getTime() - last.getTime()) / 1000 )
        console.log(toGo)
        if(toGo > 0) {
          console.log(`togo > 0: ${toGo}`)
          this.setState({
            secondsToDo: toGo
          })
      }
      }
      this.interval = setInterval(this.updateTimer, 1000)
    }
  }

  componentDidUpdate(prevProps) {
    const { secondsToDo } = this.props;
    if (secondsToDo !== prevProps.secondsToDo) {
      this.updateTimer();
    }
  }

  componentWillUnmount() {
    const { id, timerOnGo} = this.props;
    const { secondsToDo } = this.state;
    if (secondsToDo > 0) {
      localStorage.setItem(id, new Date());
    }
    timerOnGo(id, secondsToDo);
  }

  updateTimer = () => {
    const { secondsToDo } = this.state;
    const newTime = secondsToDo - 1;
    if (newTime === 0) {
      clearInterval(this.interval)
    } 
    this.setState(() => ({
      secondsToDo: newTime,
    }))
    // timerOnGo(id);
  }

  getTimeStr = () => {
    const { secondsToDo } = this.state;
    const newMin = Math.floor(secondsToDo / 60);
    const newSec = secondsToDo - newMin * 60;
    return `${newMin}:${newSec}`;
  }

  render() {
    const time = this.getTimeStr();

    return (
      <span className="description">
        <button aria-label="icon-play" type="button" className="icon icon-play" />
        <button aria-label="icon stop" type="button" className="icon icon-pause" />
        {time}
      </span>
    )
  }
};

Timer.defaultProps = {
  secondsToDo: 0
};
Timer.propTypes = {
  id: PropTypes.string.isRequired,
  secondsToDo: PropTypes.number,
  timerOnGo: PropTypes.func.isRequired
}
