import {React, Component} from "react";

import { formatDistanceToNow } from "date-fns";

import PropTypes from 'prop-types'

import './task.css'

class Task extends Component {
    render () {
        const { done, description, createdTime, onDeleted, onToggleDone } = this.props;

        let status;

        if (done) {
            status = 'completed';
        } else {
            status = 'active';
        }

        return (
            <li className={status}>
                <div className="view">
                    <input className="toggle" 
                            type="checkbox"
                            checked={done} 
                            onChange={onToggleDone}/>
                    <label>
                        <span className="description">{ description }</span>
                        <span className="created">{ `created ${formatDistanceToNow(createdTime, {includeSeconds: true})} ago` }</span>
                    </label>
                    <button className="icon icon-edit"
                            onClick={this.editingHandler}></button>
                    <button className="icon icon-destroy"
                            onClick={onDeleted}></button>
                </div>
                <input type="text" className="edit" defaultValue="Editing task" />
            </li>
        );
    }
}

Task.defaultProps = {
  done: false,
  description: 'empty task',
  createdTime: Date()
}

Task.propTypes = {
  done: PropTypes.bool,
  description: PropTypes.string,
  createdTime: PropTypes.instanceOf(Date),
  onDeleted: PropTypes.func.isRequired,
  onToggleDone: PropTypes.func.isRequired
}

export default Task;
