import React from "react";

import './tasks-filter.css'

const TasksFilter = ({filter, toFilter}) => {
    return (
        <ul className="filters">
            <li>
                <button className={filter === 'All' ? 'selected' : null}
                        onClick={toFilter('All')}>All</button>
            </li>
            <li>
                <button className={filter === 'Active' ? 'selected' : null}
                        onFocus={toFilter('Active')}>Active</button>
            </li>
            <li>
                <button className={filter === 'Completed' ? 'selected' : null}
                        onClick={(toFilter)('Completed')}>Completed</button>
            </li>
        </ul>
    );
};

export default TasksFilter;
