import {React, Component} from "react";
import NewTaskForm from "../NewTaskForm";
import AppMain from "../app-main";

import './app.css'

export default class App extends Component {

    state = {
        todosData : [
            // {
            //     id: '1',
            //     status: 'completed',
            //     description: 'drink coffee',
            //     createdTime: 'created 17 seconds ago'
            // },
            // {
            //     id: '2',
            //     status: 'active',
            //     description: 'dew app',
            //     createdTime: 'created 5 minutes ago'
            // },
            // {
            //     id: '3',
            //     status: 'active',
            //     description: 'do a training set',
            //     createdTime: 'created 5 minutes ago'
            // },
            // {
            //     id: '4',
            //     status: 'active',
            //     description: 'read a todos list',
            //     createdTime: 'created 5 minutes ago'
            // }
        ]
    }

    // maxId = this.state.todosData.length;
    maxId = 0;

    deleteTask = (id) => {
        this.setState(({ todosData }) => {
            const idx = todosData.findIndex(el => el.id === id);
            const newData = [
                ...todosData.slice(0, idx),
                ...todosData.slice(idx + 1)
            ];
            return {
                todosData: newData
            }
        })
    }

    addTask = (task) => {
        this.setState(({todosData}) => {
            this.maxId++;
            console.log(this.maxId)
            const newTask = [{id: this.maxId, status: 'active', description: task, createdTime: '228 secs ago'}]
            const newData = [...todosData, ...newTask];
            return {
                todosData: newData
            }
        })
    }

    render () {
        return (
            <section className = 'todoapp'>
                <NewTaskForm addTask = {this.addTask}/>
                <AppMain todos = {this.state.todosData}
                        onDeleted={this.deleteTask}/>
            </section>
        );
    }
}
