import {React, Component} from "react";

import { formatDistanceToNow } from "date-fns";

import PropTypes from 'prop-types'

import './task.css'

class Task extends Component {
    constructor() {
      super();
      this.state = {
        editing: false,
      };
    }

    toEditing = () => {
      this.setState(({ editing }) => ({ editing: !editing}));
      editTask(id)
    };


    render () {
        const { todo, onDeleted, onToggleDone, editTask } = this.props;
        const { id, done, description, createdTime } = todo;
        let status;

        if (done) {
            status = 'completed';
        } else {
            status = 'active';
        }

        return (
            <li className={status}>
                <div className="view">
                    <input className="toggle" 
                            id = {id}
                            type="checkbox"
                            checked={done} 
                            onChange={onToggleDone}/>
                    <label htmlFor={id}>
                        <span className="description">{ description }</span>
                        <span className="created">{ `created ${formatDistanceToNow(createdTime, {includeSeconds: true})} ago` }</span>
                    </label>
                    <button type="button"
                            aria-label="edit"
                            className="icon icon-edit"
                            onClick={this.toEditing} />
                    <button type="button"
                            aria-label= "delete"
                            className="icon icon-destroy"
                            onClick={onDeleted} />
                </div>
                <input type="text" className="edit" defaultValue="Editing task" />
            </li>
        );
    }
}

Task.defaultProps = {
  todo : {
    id: 0,
    done: false,
    description: 'empty task',
    createdTime: new Date()
  }
}

Task.propTypes = {
  todo: PropTypes.shape({
    id: PropTypes.number,
    done: PropTypes.bool,
    description: PropTypes.string,
    createdTime: PropTypes.instanceOf(Date),
  }),
  onDeleted: PropTypes.func.isRequired,
  onToggleDone: PropTypes.func.isRequired,
  editTask: PropTypes.func.isRequired
}

export default Task;
