import React, { useEffect } from 'react';

import PropTypes from 'prop-types';

export default function Timer ({ id, secondsLeft, timerStop, timerPlay }) {

  useEffect(() => {
    console.log(id, secondsLeft, isPlayed)
  }, [secondsLeft])

  const getTimeStr = () => {
    const newMin = Math.floor(secondsLeft / 60);
    const newSec = secondsLeft - newMin * 60;
    return `${newMin}:${newSec}`;
  };

  // if (!secondsToDo) {
  //   return null;
  // }
  const time = getTimeStr();

  return (
    <span className="description">
      <button
        aria-label="icon-play"
        type="button"
        className="icon icon-play"
        onClick={timerOnPlay}
      />
      <button
        aria-label="icon stop"
        type="button"
        className="icon icon-pause"
        // onClick={timerOnPause}
      />
      {time}
    </span>
  );
}

Timer.defaultProps = {
  secondsLeft: 0,
};
Timer.propTypes = {
  id: PropTypes.string.isRequired,
  secondsLeft: PropTypes.number,
  timerStop: PropTypes.func.isRequired,
  timerPlay: PropTypes.func.isRequired,
};
