import {React, Component} from "react";

import './task.css'

export default class Task extends Component {

    // state = {
    //     done: this.props.status === 'completed' ? true : false,
    //     editing: this.props.status === 'editing' ? true : false,
    // }

    // checkboxHandler = () => {
    //     this.setState(state => {
    //         return {
    //             done: !this.state.done 
    //         }; 
    //     });
    // };

    editingHandler = () => { // /!TODO !!!!
        this.setState(state => {
            return {
                done: this.state.done === true ? false : false,
                editing: !this.state.editing
            };
        });
    };

    render () {
        let { done, status, description, createdTime, onDeleted, onToggleDone } = this.props;

        return (
            <li className={status}>
                <div className="view">
                    <input className="toggle" 
                            type="checkbox"
                            checked={done ? true : false} 
                            onChange={onToggleDone}/>
                    <label>
                        <span className="description">{ description }</span>
                        <span className="created">{ createdTime }</span>
                    </label>
                    <button className="icon icon-edit"
                            onClick={this.editingHandler}></button>
                    <button className="icon icon-destroy"
                            onClick={onDeleted}></button>
                </div>
                <input type="text" className="edit" defaultValue="Editing task" />
            </li>
        );
    }
}
